$(document).ready(function(){$(".group1").colorbox({rel:'group1'});$(".group2").colorbox({rel:'group2',transition:"fade"});$(".group3").colorbox({rel:'group3',transition:"none",width:"70%",height:"90%"});$(".group4").colorbox({rel:'group4',slideshow:true,slideshowAuto:false});$(".ajax").colorbox();$(".youtube").colorbox({iframe:true,innerWidth:425,innerHeight:344});$(".iframe").colorbox({iframe:true,width:"80%",height:"80%"});$(".inline").colorbox({inline:true,width:"auto"});$(".callbacks").colorbox({onOpen:function(){alert('onOpen: colorbox is about to open');},onLoad:function(){alert('onLoad: colorbox has started to load the targeted content');},onComplete:function(){alert('onComplete: colorbox has displayed the loaded content');},onCleanup:function(){alert('onCleanup: colorbox has begun the close process');},onClosed:function(){alert('onClosed: colorbox has completely closed');}});$("#click").click(function(){$('#click').css({"background-color":"#f00","color":"#fff","cursor":"inherit"}).text("Open this window again and this message will still be here.");return false;});});